import React from "react";
import { Box, Typography } from "@material-ui/core";
import { makeStyles } from "@material-ui/core/styles";

const useStyles = makeStyles((theme) => ({
  root: {
    display: "flex",
    justifyContent: "space-between",
    marginLeft: 20,
    flexGrow: 1,
  },
  username: {
    fontWeight: "bold",
    letterSpacing: -0.2,
  },
  previewText: {
    fontSize: 12,
    color: "#9CADC8",
    letterSpacing: -0.17,
  },
  boldText: {
    fontSize: 14,
    fontWeight: 700,
    color: 	"#000000",
    letterSpacing: -0.17
  }
}));

const ChatContent = (props) => {
  const classes = useStyles();

  const { conversation } = props;
  const { latestMessageText, otherUser } = conversation;

  console.log("Inside ChatContent", conversation);
  const unreadMsgs = conversation.messages && conversation.messages.reduce((acc, msg) => {
    console.log(msg);
    if (!msg.msgRead) {
      return acc++;
    }
    return acc;
  }, 0);


  console.log("unreadMsgs", unreadMsgs);

  return (
    <Box className={classes.root}>
      <Box>
        <Typography className={classes.username}>
          {otherUser.username}
        </Typography>
        <Typography className={(unreadMsgs) ? classes.boldText : classes.previewText}>
          {latestMessageText}
        </Typography>
      </Box>
    </Box>
  );
};

export default ChatContent;
