import React from "react";
import { Badge, Box } from "@material-ui/core";

const UnreadMessageBadge = (props) => {
  const { unreadCount } = props;

  return (
    <>
      {(unreadCount > 0) &&
      <Box>
         <Badge badgeContent={unreadCount} color="primary" />
      </Box>
      }
    </>
  );
};

export default UnreadMessageBadge;
