import React from "react";
import { Box, Typography } from "@material-ui/core";
import { BadgeAvatar, ChatContent } from "../Sidebar";
import { makeStyles } from "@material-ui/core/styles";
import { setActiveChat } from "../../store/activeConversation";
import { connect } from "react-redux";

const useStyles = makeStyles((theme) => ({
  root: {
    borderRadius: 8,
    height: 80,
    boxShadow: "0 2px 10px 0 rgba(88,133,196,0.05)",
    marginBottom: 10,
    display: "flex",
    alignItems: "center",
    "&:hover": {
      cursor: "grab"
    }
  },
  bubble: {
    backgroundColor: "#3A8DFF",
    borderRadius: "50%",
  },
  text: {
    fontSize: 14,
    color: "#ffffff",
    padding: ".2rem .6rem",
    fontWeight: "bold"
  },
}));

const Chat = (props) => {
  const classes = useStyles();
  const { conversation } = props;
  const { otherUser } = conversation;

  const handleClick = async (conversation) => {
    await props.setActiveChat(conversation.otherUser.username);
    await markRead(conversation.id);
  };

  const unreadCount = conversation.messages && conversation.messages.reduce((acc, msg) => {
    console.log(msg);
    console.log('otherUser', otherUser.id);
    if (!msg.msgRead && msg.senderId === otherUser.id) {
      return acc + 1;
    }
    return acc;
  }, 0);

  return (
    <Box onClick={() => handleClick(conversation)} className={classes.root}>
      <BadgeAvatar
        photoUrl={otherUser.photoUrl}
        username={otherUser.username}
        online={otherUser.online}
        sidebar={true}
      />
      <ChatContent conversation={conversation} unread={(unreadCount > 0) ? true: false} />
      { (unreadCount > 0) &&
        <Box className={classes.bubble}>
          <Typography className={classes.text}>
            {unreadCount}
          </Typography>
        </Box>
      }
    </Box>
  );
};

const mapDispatchToProps = (dispatch) => {
  return {
    setActiveChat: (id) => {
      dispatch(setActiveChat(id));
    },
    markRead: (id) => {
      dispatch(markMessagesRead(id));
    }
  };
};

export default connect(null, mapDispatchToProps)(Chat);
