import React from "react";
import { Box } from "@material-ui/core";
import { SenderBubble, OtherUserBubble } from "../ActiveChat";
import moment from "moment";
import OtherUserAvatar from "./OtherUserAvatar";

const Messages = (props) => {
  const { messages, otherUser, userId } = props;
  const firstUnreadMessage = messages.findIndex(msg => msg.msgRead === false);
  const lastReadMessage = (firstUnreadMessage === -1)
    ? messages[messages.length - 1].id
    : messages[firstUnreadMessage - 1].id;

  console.log(lastReadMessage)

  return (
    <Box>
      {messages.map((message) => {
        const time = moment(message.createdAt).format("h:mm");

        return message.senderId === userId ? (
          <div key={message.id}>
            <SenderBubble text={message.text} time={time} />
            { message.id === lastReadMessage &&
              <OtherUserAvatar otherUser={otherUser} />}
          </div>
        ) : (
          <OtherUserBubble key={message.id} text={message.text} time={time} otherUser={otherUser} />
        );
      })}
      {(messages.length > 0) &&
        messages[messages.length - 1].senderId === userId &&
        messages[messages.length - 1].msgRead === true &&
        <OtherUserAvatar otherUser={otherUser} />
      }
    </Box>
  );
};

export default Messages;
