import { createMuiTheme } from "@material-ui/core";

export const theme = createMuiTheme({
  typography: {
    fontFamily: "Open Sans, sans-serif",
    fontSize: 14,
    button: {
      textTransform: "none",
      letterSpacing: 0,
      fontWeight: "bold"
    }
  },
  overrides: {
    MuiInput: {
      input: {
        fontWeight: "bold"
      }
    }
  },
  palette: {
    primary: { main: "#3A8DFF" },
    secondary: { main: "#B0B0B0" }
  }
});

export const impactTheme = createMuiTheme({
  ...theme,
  typography: {
    fontWeight: 700
  },
  palette: {
    type: "impact",
    primary: { main: "#000000" }
  },
});

export const grayTheme = createMuiTheme({
  ...theme,
  palette: {
    type: "plain",
    primary: { main: "#9CADC8" }
  }
});
