import React from "react";
import { Box } from "@material-ui/core";
import { BadgeAvatar, ChatContent, UnreadMsgCount } from "../Sidebar";
import { makeStyles } from "@material-ui/core/styles";
import { setActiveChat } from "../../store/activeConversation";
import { connect } from "react-redux";
import { markMessagesRead } from "../../store/utils/thunkCreators";

const useStyles = makeStyles((theme) => ({
  root: {
    borderRadius: 8,
    height: 80,
    boxShadow: "0 2px 10px 0 rgba(88,133,196,0.05)",
    marginBottom: 10,
    display: "flex",
    alignItems: "center",
    "&:hover": {
      cursor: "grab"
    }
  },
  bubble: {
    backgroundColor: "#3A8DFF",
    borderRadius: "50%",
  },
  text: {
    fontSize: 14,
    color: "#ffffff",
    padding: ".2rem .6rem",
    fontWeight: "bold"
  },
}));

const Chat = (props) => {
  const classes = useStyles();
  const { conversation } = props;
  const { otherUser } = conversation;

  const unreadCount = conversation.messages &&
    conversation.messages.reduce((acc, msg) => {
    if (!msg.msgRead && msg.senderId === otherUser.id) {
      return acc + 1;
    }
    return acc;
  }, 0);

  const handleClick = async (conversation) => {
    await props.setActiveChat(conversation.otherUser.username);

    if (unreadCount > 0) {
      let unreadMsgs = [];
      for (let i = 0; i < conversation.messages.length; i++) {
        let msg = conversation.messages[i];
        if (!msg.msgRead && msg.senderId === otherUser.id) {
          unreadMsgs.push(msg.id);
        }
        await markMessagesRead(unreadMsgs);
      }
    }
  };

  return (
    <Box onClick={() => handleClick(conversation)} className={classes.root}>
      <BadgeAvatar
        photoUrl={otherUser.photoUrl}
        username={otherUser.username}
        online={otherUser.online}
        sidebar={true}
      />
      <ChatContent conversation={conversation} unreadCount={unreadCount} />
      <UnreadMsgCount unreadCount={unreadCount}/>
    </Box>
  );
};

const mapDispatchToProps = (dispatch) => {
  return {
    setActiveChat: (id) => {
      dispatch(setActiveChat(id));
    },
    markMessagesRead: (conversation) => {
      dispatch(markMessagesRead(conversation));
    }
  };
};

export default connect(null, mapDispatchToProps)(Chat);
